$(document).ready(function () {
  if ($("a[href='#form'")) {
    $("a[href='#form']").bind('click', function (event) {
      $('html,body').stop().animate({ scrollTop: $("#form").offset().top }, 'slow');
      event.preventDefault();
      return false;
    });
  }
});

$('.js-slider2').owlCarousel({
  loop:true,
  margin:35,
  nav:true,
  dotsContainer: '.slider2__dot',
  responsive:{
      0:{
          items:1
      },
      600:{
          items:2
      },
      1000:{
          items:3
      }
  }
});
