$(document).ready(function () {
  if ($("a[href='#form'")) {
    $("a[href='#form']").bind('click', function (event) {
      $('html,body').stop().animate({ scrollTop: $("#form").offset().top }, 'slow');
      event.preventDefault();
      return false;
    });
  }
});

$('.js-slider2').owlCarousel({
  loop: true,
  margin: 35,
  nav: true,
  navContainer: '.slider2__nav',
  responsive: {
    0: {
      items: 1
    },
    600: {
      items: 2
    },
    1000: {
      items: 3
    }
  }
})

function modal() {
  const [...btns] = document.querySelectorAll('[data-open-modal]');
  const [...closeBtns] = document.querySelectorAll('[data-close]');

  btns.forEach((el) => {
    el.addEventListener('click', function (e) {
      const dataVal = e.currentTarget.dataset.openModal;
      const currentModal = document.querySelector('[data-modal=' + dataVal + ']');

      if (!currentModal) return;

      currentModal.classList.add('active');
    });
  });

  closeBtns.forEach((el) => {
    el.addEventListener('click', function (e) {
      const closeEl = e.currentTarget;

      closeEl.closest('.modal-item').classList.remove('active');
    });
  });
}

modal();
